import React, { useState, useEffect } from "react";
import { Meme } from "./components/Meme";
import styles from "./mystyle.module.css";
import {
  FacebookShareButton,
  FacebookIcon,
  TwitterShareButton,
  TwitterIcon,
  TumblrShareButton,
  TumblrIcon,
  TelegramShareButton,
  TelegramIcon,
  WhatsappShareButton,
  WhatsappIcon,
  RedditShareButton,
  RedditIcon,
} from "react-share";

const objectToQueryParam = (obj) => {
  const params = Object.entries(obj).map(([key, value]) => `${key}=${value}`);

  return "?" + params.join("&");
};

function MemeGenerator() {
  const [memeTemplates, setTemplates] = useState([]);
  const [memeTemplate, setTemplate] = useState(null);
  const [topText, setTopText] = useState("");
  const [bottomText, setBottomText] = useState("");
  const [meme, setMeme] = useState(null);

  useEffect(() => {
    fetch("https://api.imgflip.com/get_memes").then((x) =>
      x.json().then((response) => setTemplates(response.data.memes))
    );
  }, []);

  if (meme) {
    return (
      <SocialMediaShare></SocialMediaShare>

      // <div style={{ textAlign: "center" }}>
      //   <img style={{ width: 200 }} src={meme} alt="custom meme" />
      //   {/* add share buttons */}
      //   <p>Share:</p>
      //   <form
      //     onClick={async (e) => {
      //       e.preventDefault();
      //       console.log("refresh");
      //       window.location.reload();
      //     }}
      //   >
      //     <FacebookShareButton url={meme}>
      //       <FacebookIcon size={36} />
      //     </FacebookShareButton>
      //     <TwitterShareButton url={meme}>
      //       <TwitterIcon size={36} />
      //     </TwitterShareButton>
      //     <TumblrShareButton url={meme}>
      //       <TumblrIcon size={36} />
      //     </TumblrShareButton>
      //     <WhatsappShareButton url={meme}>
      //       <WhatsappIcon size={36} />
      //     </WhatsappShareButton>
      //     <TelegramShareButton url={meme}>
      //       <TelegramIcon size={36} />
      //     </TelegramShareButton>
      //     <RedditShareButton url={meme}>
      //       <RedditIcon size={36} />
      //     </RedditShareButton>
      //     <p></p>
      //   </form>
      // </div>
    );
  }
  return (
    <div style={{ textAlign: "center" }}>
      {memeTemplate && (
        <form
          onSubmit={async (e) => {
            e.preventDefault();
            // add logic to create meme from api
            const params = {
              template_id: memeTemplate.id,
              text0: topText,
              text1: bottomText,
              username: "matan.atti",
              password: "m12345678",
            };
            const response = await fetch(
              `https://api.imgflip.com/caption_image${objectToQueryParam(
                params
              )}`
            );
            const result = await response.json();
            console.log("result: ", result);
            if (!result.success) {
              setMeme(null);
            }
            if (result.success) {
              setMeme(result.data.url);

              localStorage.setItem(Date.now(), result.data.url);
            }
          }}
        >
          <Meme template={memeTemplate} />
          <p></p>

          <input
            maxLength="50"
            className={styles.topText}
            placeholder="top text"
            value={topText}
            onChange={(e) => setTopText(e.target.value)}
          />

          <input
            maxLength="50"
            className={styles.bottomText}
            placeholder="bottom text"
            value={bottomText}
            onChange={(e) => setBottomText(e.target.value)}
          />
          <p></p>
          <button className={styles.rainbowButton} type="submit">
            Create{" "}
          </button>
        </form>
      )}
      {!memeTemplate && (
        <>
          {memeTemplates.map((template) => {
            return (
              <Meme
                template={template}
                onClick={() => {
                  setTemplate(template);
                }}
              />
            );
          })}
        </>
      )}
    </div>
  );
}

export default MemeGenerator;
