import React, { useState, useEffect } from "react";
import "./App.css";
import { Meme } from "./components/Meme";
import { HistoryMemes } from "./components/HistoryMemes";
import {
  FacebookShareButton,
  FacebookIcon,
  TwitterShareButton,
  TwitterIcon,
  TumblrShareButton,
  TumblrIcon,
  TelegramShareButton,
  TelegramIcon,
  WhatsappShareButton,
  WhatsappIcon,
  RedditShareButton,
  RedditIcon,
} from "react-share";

const objectToQueryParam = (obj) => {
  const params = Object.entries(obj).map(([key, value]) => `${key}=${value}`);

  return "?" + params.join("&");
};
const addItem = (e) => {
  if (this.meme.value !== "") {
    var newItem = {
      text: this.meme.value,
      key: Date.now(),
    };
    memes.push(newItem);
  }
  this._inputElement.value = "";
  console.log("items: ", this.state.items);
};

function App() {
  const [templates, setTemplates] = useState([]);
  const [template, setTemplate] = useState(null);
  const [topText, setTopText] = useState("");
  const [bottomText, setBottomText] = useState("");
  const [meme, setMeme] = useState(null);
  const [memes, setMemes] = useState([]);

  useEffect(() => {
    fetch("https://api.imgflip.com/get_memes").then((x) =>
      x.json().then((response) => setTemplates(response.data.memes))
    );
  }, []);

  if (meme) {
    return (
      <div style={{ textAlign: "center" }}>
        <h1> Meme Generator </h1>
        <img style={{ width: 200 }} src={meme} alt="custom meme" />
        {/* add share buttons */}
        <p></p>
        <form
          onClick={async (e) => {
            e.preventDefault();
            console.log("refresh");
            window.location.reload();
          }}
        >
          <FacebookShareButton url={meme}>
            <FacebookIcon size={36} />
          </FacebookShareButton>
          <TwitterShareButton url={meme}>
            <TwitterIcon size={36} />
          </TwitterShareButton>
          <TumblrShareButton url={meme}>
            <TumblrIcon size={36} />
          </TumblrShareButton>
          <WhatsappShareButton url={meme}>
            <WhatsappIcon size={36} />
          </WhatsappShareButton>
          <TelegramShareButton url={meme}>
            <TelegramIcon size={36} />
          </TelegramShareButton>
          <RedditShareButton url={meme}>
            <RedditIcon size={36} />
          </RedditShareButton>
          <p></p>
        </form>
      </div>
    );
  }
  return (
    <div style={{ textAlign: "center" }}>
      <h1> Meme Generator </h1>
      {template && (
        <form
          onSubmit={async (e) => {
            e.preventDefault();
            // add logic to create meme from api
            const params = {
              template_id: template.id,
              text0: topText,
              text1: bottomText,
              username: "matan.atti",
              password: "m12345678",
            };
            const response = await fetch(
              `https://api.imgflip.com/caption_image${objectToQueryParam(
                params
              )}`
            );
            const result = await response.json();
            console.log("result: ", result);
            if (!result.success) {
              setMeme(null);
            }
            if (result.success) {
              setMeme(result.data.url);
              memes.push(result.data.url);

              addItem(result.data.url);
              console.log("history memes: ", memes);

              setMemes(memes);
            }
          }}
        >
          <Meme template={template} />

          <input
            placeholder="top text"
            value={topText}
            onChange={(e) => setTopText(e.target.value)}
          />
          <input
            placeholder="bottom text"
            value={bottomText}
            onChange={(e) => setBottomText(e.target.value)}
          />
          <button type="submit">create </button>
        </form>
      )}
      {!template && (
        <>
          {templates.map((template) => {
            return (
              <Meme
                template={template}
                onClick={() => {
                  setTemplate(template);
                }}
              />
            );
          })}
        </>
      )}
    </div>
  );
}

export default App;
